package main

import (
	"encoding/json"
	"fmt"
	"log"
	"math/rand"
	"net/http"
	"strconv"

	"github.com/gorilla/mux"
)

type Temple struct {
	ID          string `json:"id"`
	Name        string `json:"name"`
	Location    string `json:"location"`
	Description string `json:"description"`
}

var temples []Temple

func setCorsHeaders(next http.Handler) http.Handler {
	return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		w.Header().Set("Access-Control-Allow-Origin", "*")
		w.Header().Set("Access-Control-Allow-Methods", "GET, POST, PUT, DELETE")
		w.Header().Set("Access-Control-Allow-Headers", "Content-Type, Authorization")

		if r.Method == "OPTIONS" {
			w.WriteHeader(http.StatusOK)
			return
		}

		next.ServeHTTP(w, r)
	})
}

func getTemples(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-type", "application/json")
	json.NewEncoder(w).Encode(temples)
}

func getTemple(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-type", "application/json")
	params := mux.Vars(r)
	for _, item := range temples {
		if item.ID == params["id"] {
			json.NewEncoder(w).Encode(item)
			return
		}
	}
	http.NotFound(w, r)
}

func createTemple(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-type", "application/json")
	var temple Temple
	_ = json.NewDecoder(r.Body).Decode(&temple)
	temple.ID = strconv.Itoa(rand.Intn(100000000))
	temples = append(temples, temple)
	json.NewEncoder(w).Encode(temples)
}

func updateTemple(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-type", "application/json")
	params := mux.Vars(r)
	for index, item := range temples {
		if item.ID == params["id"] {
			temples = append(temples[:index], temples[index+1:]...)
			var temple Temple
			_ = json.NewDecoder(r.Body).Decode(&temple)
			temple.ID = params["id"]
			temples = append(temples, temple)
			json.NewEncoder(w).Encode(temples)
			return
		}
	}
	http.NotFound(w, r)
}

func deleteTemple(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-type", "application/json")
	params := mux.Vars(r)
	for index, item := range temples {
		if item.ID == params["id"] {
			temples = append(temples[:index], temples[index+1:]...)
			json.NewEncoder(w).Encode(temples)
			return
		}
	}
	http.NotFound(w, r)
}

